mapper.select:
select board_id, board_title, user_id, board_nickName, board_created_at, reply_count, view_count, likes, dislikes from board
WHERE board_title LIKE CONCAT('%', #{keyword}, '%') OR board_content LIKE CONCAT('%', #{keyword}, '%')
WHERE user_id = #{keyword}
ORDER BY board_created_at DESC LIMIT #{pageStart}, #{amount}

mapper.update:
UPDATE board
SET board_content = #{boardContent},
board_updated_at = #{boardUpdatedAt}
WHERE board_id = #{boardId}

UPDATE board b
SET b.reply_count = (
SELECT COUNT(*)
FROM reply r
WHERE b.board_id = r.board_id
);

UPDATE board
SET view_count = view_count + 1
WHERE board_id = #{boardId}

UPDATE board
SET likes = likes + 1
WHERE board_id = #{boardId}

UPDATE board
SET dislikes = dislikes + 1
WHERE board_id = #{boardId}
